/* * Sistemas Distribuídos * Profº. Rodrigo Campiolo * Emerson Yudi Nakashima 1451600 * Gustavo Correia Gonzalez 1551787 * TCPServer: Servidor para conexao TCP com Threads * Descricao: Recebe uma conexao e cria uma thread para a conversação */import java.io.IOException;import java.net.ServerSocket;import java.net.Socket;public class TCPServer {		public TCPServer () throws InterruptedException, IOException {		int serverPort = 7896;		ServerSocket listenSocket = new ServerSocket(serverPort);				Runnable run = new Runnable() {			@Override			public void run() {				try {					newConnection(listenSocket);				} catch (IOException | InterruptedException e) {					e.printStackTrace();				}			}		};		Thread connection = new Thread(run);		connection.start();			}		private void newConnection(ServerSocket listenSocket) throws IOException, InterruptedException {		while(true) {			Socket connectionSocket = listenSocket.accept();			new SendReceiveThread(connectionSocket);		}	}	}